import './App.css';
import Elixirs from './Elixirs';

function App() {
  const yellowTxt = `
  허브, 미네랄, 부드러운 감귤에 레몬을 섞은 비타민이 
  풍부한 최고의 음료로 하루 종일 면역 계통을 최상의 
  상태로 유지할 수 있어요.
  `
  const chaiTxt = `
  일반 차이티가 아닙니다. 이 음료는 마테차에 차이티 
  성분을 섞은 다음 얼음에 카페인 맛이 나는 초콜릿을 살짝 넣은 음료입니다.
  `
  return (
    <div id="elixirs">
      <div id="headerTxt">
        주간 음료 스페셜
      </div>
      <Elixirs src="./images/yellow.gif" title="레몬 브리즈" />
      <Elixirs />
      <Elixirs />
    </div>
  );
}

export default App;
